import 'package:bloc/bloc.dart';import 'package:equatable/equatable.dart';import 'package:floges_flutter_app/data/global_data.dart';import 'package:floges_flutter_app/data/home_data_model.dart';import 'package:floges_flutter_app/domain/api_services.dart';import 'package:floges_flutter_app/presentation/widgets/toast.dart';part 'home_bloc_state.dart';class HomeBlocCubit extends Cubit<HomeBlocState> {  HomeBlocCubit() : super(HomeBlocInitial());  ///employee list  getEmployees({Map<String, String> params}) async {    try {      emit(Loading());      var res = await ApiServices().getEmployeeList(params: params);      HomeDataModel homeDataModel = homeDataModelFromMap(res.data);      if (GlobalData?.homeDataModel?.data == null) {        GlobalData.homeDataModel = homeDataModel;      } else {        GlobalData.homeDataModel.totalPages = homeDataModel.totalPages;        homeDataModel.data.forEach((element) {          GlobalData.homeDataModel.data.add(element);        });      }      emit(HomeDataResponse(GlobalData.homeDataModel));    } catch (e) {      showToast(msg: 'error');      emit(DataError(e));    }  }}